let numCitizens = 500

class Settlement {
 constructor(name, founded) {
  this.name = name;
  this.founded = founded
 }
}

const settingsVillage = {
 hospital: false,
 mall: false,
}

const settingsTown = {
 hospital: false,
 mall: true,
}

const settingsCity = {
 hospital: true,
 mall: true,
}

if (numCitizens < 1000)
 Object.assign(Settlement.prototype, settingsVillage);
else if (numCitizens < 10000)
 Object.assign(Settlement.prototype, settingsTown);
else
 Object.assign(Settlement.prototype, settingsCity);


let mySettlement = new Settlement('Dumperdingen', 1812);
console.log(mySettlement.mall);
