const handleSuccess = evt => {
 const db = evt.target.result;

 let myObjectStore = db.transaction(objStoreName, 'readwrite')
  .objectStore(objStoreName);

 // Objekt mit ID 13 lesen
 let myRequest = myObjectStore.get(13)

 myRequest.addEventListener('success', evt => {
  const data = evt.target.result;

  // Datensatz ändern
  data.leistungKW = 210;

  // Datesatz schreiben
  const requestUpdate = myObjectStore.put(data);
  requestUpdate.addEventListener('success', evt => {
   console.log('Data updated', data);
  });
 })
}

const init = () => {
 requestDB = window.indexedDB.open(dbName, 5);
 requestDB.addEventListener('success', handleSuccess);
}

init();
