const meinAuto = {
 marke: 'Hyundai',
 modell:'i20',
 leistungKW: 62,
}

const newCar = {...meinAuto};

meinAuto.leistungKW = 100;

console.log(meinAuto);  // -> { marke: 'Hyundai', modell: 'i20', leistungKW: 100 }
console.log(newCar);    // -> { marke: 'Hyundai', modell: 'i20', leistungKW: 62 }

const meinAuto = {
 marke: 'Hyundai',
 modell:'i20',
 leistungKW: 62,
}

const luxusFeatures = {
 farbe: 'Perlgrau',
 klima: true    
}

const newCar = {...meinAuto, ...luxusFeatures, baujahr: 2020};

console.log(newCar);  // -> {
                      // ->  marke: 'Hyundai',
                      // ->  modell: 'i20',
                      // ->  leistungKW: 62,
                      // ->  farbe: 'Perlgrau',
                      // ->  klima: true,
                      // ->  baujahr: 2020
                      // -> }
