const handleEvent = evt => console.log(evt.currentTarget);

document.querySelectorAll('*').forEach(el =>
 el.addEventListener('click', handleEvent) 
 // -> &lt;section id="third">
 // -> &lt;main>
 // -> &lt;body>
 // -> &lt;html lang="de">

 // Alternative 1
 el.addEventListener('click', handleEvent, true) 
 // -> &lt;html lang="de">
 // -> &lt;body>
 // -> &lt;main>
 // -> &lt;section id="third">

 // Alternative 2
 // el.addEventListener('click', handleEvent, { capture: true })

)
