const c = document.querySelector('#myCanvas');
const ctx = c.getContext('2d');

ctx.beginPath();
ctx.rect(20, 20, 50, 110);
ctx.rect(100, 20, 90, 110);
ctx.rect(220, 20, 50, 110);
ctx.fill();

c.addEventListener('mousemove', evt => {
 console.log(ctx.isPointInPath(evt.layerX, evt.layerY));
})

const c = document.querySelector('#myCanvas');
const ctx = c.getContext('2d');

let myPath = new Path2D();
myPath.moveTo(30, 30);
myPath.lineTo(60, 20);
myPath.lineTo(150, 90);
myPath.lineTo(240, 20);
myPath.lineTo(270, 30);
myPath.lineTo(150, 120);
myPath.closePath();
ctx.fill(myPath);

c.addEventListener('mousemove', evt => {
 console.log(ctx.isPointInPath(myPath, evt.layerX, evt.layerY));
})
