const template = () => {
 const elTemplate = document.createElement('div');
 template.className = 'container';

 const elHeader = document.createElement('h3');
 elHeader.innerHTML = 'A Title';
 elTemplate.append(elHeader);

 const elText = document.createElement('p');
 elText.innerHTML = 'A Content';
 elTemplate.append(elText);

 // STYLE
 const elStyle = document.createElement('link');
 elStyle.rel = 'stylesheet';  

 // Aktuelle Adresse der JSDatei auslesen
 let localURL =  new URL(import.meta.url).pathname; 

 // Dateiname der JS-Datei durch den Namen der CSS-Da tei ersetzen
 let indexLastSlash = localURL.lastIndexOf('/');
 localURL = localURL.substring(0, indexLastSlash + 1);
 localURL += 'styles.css'; 

 // CSS-Datei laden
 elStyle.href = localURL;
 template.append(elStyle);

 return {elTemplate, elHeader, elText}
}

export default template;

/* styles.css */

.container { 
 position: relative;
 display: block;
 background-color: hsl(30,80%,80%);
 padding: 5px;
 border: 1px solid rgba(0,0,0,.5);
}

