// Exportierendes Script
const kreis = {
 flaeche(radius){
  return radius ** 2 * Math.PI;
 },
 umfang(radius){
  return radius * 2 * Math.PI;
 },
}
export default kreis;

import kreis from './kreis.js';

let umfang = kreis.flaeche(10);
console.log(umfang);             // -> 314.1592653589793

// kreis.js
'use strict';

const kreis = { radius:10 };

export default kreis;

// mymodule.js
'use strict';

import kreis from './kreis.js';
kreis.radius *= 2;
const myModule = {};

export default myModule;

// index.js (Hauptdatei)
import kreis from './kreis.js';
import myModule from './mymodule.js';

console.log(kreis.radius);  // -> 20
