const c = document.querySelector('#myCanvas');
const ctx = c.getContext('2d');

const cRaster = document.createElement('canvas');
const ctxRaster = cRaster.getContext('2d')
cRaster.width = 5;
cRaster.height = 5;

// Raster zeichnen
ctxRaster.lineWidth = 1;
ctxRaster.beginPath();
ctxRaster.moveTo(0, 0);
ctxRaster.lineTo(cRaster.width, cRaster.height);
ctxRaster.stroke();

// Gerasterte Box
let raster = ctx.createPattern(cRaster, 'repeat');
ctx.fillStyle = raster;
ctx.fillRect(20, 20, c.width - 40, c.height - 40);

let raster = ctx.createPattern(cRaster, 'repeat');
let matrix = new DOMMatrix();
matrix = matrix.rotate(45).scale(3);
raster.setTransform(matrix);
ctx.fillStyle = raster;
ctx.fillRect(20, 20, c.width - 40, c.height - 40);
