const App = () => {
 const myFunction = () => {
  alert('Ich bin die Callback-Funktion');
 }
 return (
  &lt;>
   &lt;Btn callback={myFunction} content="Click Me" />
   &lt;p>{~~(Math.random()*100)}</p>
  &lt;/>
 )
}

const Btn = props => {
 return (
  &lt;>
   &lt;p>
    &lt;button onClick={props.callback}>
     {props.content}
    &lt;/button> 
   &lt;/p>
  &lt;/>
 )
}
