const App = () => {
 const myFunction = () => {
  alert('Ich bin die Callback-Funktion');
 }
 return (
  &lt;>
   &lt;Btn callback={myFunction} content="Click Me" />
   &lt;p>{~~(Math.random()*100)}</p>
  &lt;/>
 )
}

const Btn = props => {
 return (
  &lt;>
   &lt;p>
    &lt;button onClick={props.callback}>
     {props.content}
    &lt;/button> 
   &lt;/p>
  &lt;/>
 )
}

import {useState} from 'react';

const Showable = () => {
 const [value, setValue] = useState(20);

 const handleChange = evt => {
  setValue(evt.target.value)
 };

 return (
  &lt;>
   &lt;div>
    &lt;input value={value} type="range" onChange={handleChange}/>
    {(value > 80) && &lt;p>Over 80&lt;/p>}
    {(value &lt;= 80) && &lt;p>Under 80&lt;/p>}
   &lt;/div>
  &lt;/>
 );
};

export default Showable;
