let x = ~~(Math.random() * 10);
let threshold = 5;

if (x < threshold) {
    console.log(`${x} ist kleiner als ${threshold}`);
} else if (x > threshold) {
    console.log(`${x} ist größer als ${threshold}`);
} else {
    console.log(`${x} ist gleich ${threshold}`);
}

let x = ~~(Math.random() * 100);

if (x < 20) {
    console.log(`${x} ist kleiner als 20`);
} else if (x < 40) {
    console.log(`${x} ist kleiner als 40`);
} else if (x < 60) {
    console.log(`${x} ist kleiner als 60`);
} else if (x < 80) {
    console.log(`${x} ist kleiner als 80`);
} else {
    console.log(`${x} ist kleiner als 100`);
}

