import { configureStore } from '@reduxjs/toolkit';
import catalogReducer from './slices/catalogSlice.js';

export const store = configureStore({
 reducer: {
  catalog: catalogReducer
 },
});

import {createSlice} from '@reduxjs/toolkit';

const initialState =  {
 items: []
};

const catalogSlice = createSlice({
 name: 'catalog',
 initialState,
 reducers: {
  setItems: (state, action) => {
   state.items = action.payload;
  },
  getItemById: (state, action) => {
   return state.items.find(item => item.id === action.payload);
  },
  getItemsByPriceRange: (state, action) => {
   const { min, max } = action.payload;
   return state.items.filter(item => item.price >= min && item.price <= max);
  }
 },
});

export const { setItems } = catalogSlice.actions;
export default catalogSlice.reducer;

import { useSelector } from 'react-redux';

export const Catalog = () => {
 const products = useSelector((state) => state.catalog.items);

 return (
  &lt;div>
   &lt;h2>Katalog&lt;/h2>
    &lt;ul>
     {products.map((product) => (
      &lt;li key={product.id}>
       {product.name} – {product.price}€
       &lt;button>
        In Warenkorb
       &lt;/button>
      &lt;/li>
     ))}
    &lt;/ul>
   &lt;/div>
  );
}

import React, {useEffect} from 'react';
import {useDispatch} from 'react-redux';
import {setItems} from './store/slices/catalogSlice.js';
import {Catalog} from './Catalog.jsx';

const App = () => {
 const dispatch = useDispatch();

 useEffect(() => {
  fetch('/catalog.json')
   .then(response => response.json())
   .then(data => {
    dispatch(setItems(data));
   })
   .catch(error => console.error('Error loading catalog:', error));
  }, [dispatch]);

  return (
   &lt;div className="App">
    &lt;Catalog />
   &lt;/div>
  );
}

export default App;
