function Auto(marke, modell, farbe, leistungKW){
 Object.assign(this, {marke, modell, farbe, leistungKW});
 this.leistungPS = function () {
  return Math.round(this.leistungKW / .73);
 }
}

const myAuto = new Auto('VW', 'ID3', 'Weiß', 100);

console.log(myAuto);  // -> farbe: "Weiß"
                      // -> ​leistungKW: 100
                      // -> ​marke: "VW"
                      // -> ​modell: "ID3"

function Auto(marke, modell, leistungKW) {
 Object.assign(this, { marke, modell, leistungKW });

 Object.defineProperty(this, 'leistungPS', {
  get: function () {
   return Math.round(this.leistungKW / .73);
  }
 });

}

const myCar = new Auto('VW', 'id4', 140);

console.log(myCar);
console.log(myCar.leistungPS);
