// Punkte definieren
let x1 = 20, y1 = 20;
let x2 = c.width - 20, y2 = 20;
let xEnd = c.width - 20, yEnd = c.height - 20;

// Zeichnen
ctx.beginPath();
ctx.moveTo(20, c.height - 20);
ctx.bezierCurveTo(
 x1, y1,
 x2, y2,
 xEnd, yEnd
)
ctx.stroke();

// Stützpunkte zeichnen
ctx.fillStyle = '#f00';
ctx.fillRect(x1 - 2, y1 - 2, 4, 4);
ctx.fillRect(x2 - 2, y2 - 2, 4, 4);
ctx.fillStyle = '#0f0';
ctx.fillRect(xEnd - 2, yEnd - 2, 4, 4);

// Punkte definieren
let x1 = c.width / 2, y1 = 20;
let xEnd = c.width - 20, yEnd = c.height - 20;

// Zeichnen
ctx.beginPath();
ctx.moveTo(20, c.height - 20);
ctx.quadraticCurveTo(
 x1, y1,
 xEnd, yEnd
)
ctx.stroke();

// Stützpunkte zeichnen
ctx.fillStyle = '#f00';
ctx.fillRect(x1 - 2, y1 - 2, 4, 4);
ctx.fillStyle = '#0f0';
ctx.fillRect(xEnd - 2, yEnd - 2, 4, 4);
