import img from './img/nature_01.png';

const App = () => {
 return (
  &lt;>
   &lt;p>
    &lt;img src={img} width="500" />
   &lt;/p>
  &lt;/>
 )
}

import './App.css';
import { useState } from 'react';
import poems from './data/pluto.json';

const App = () => {
 // States
 let [content] = useState(poems.poemsAboutPluto);

 // Funktionen
 const createContents = () => content.map(
  (text, index) => &lt;Absatz content={text} key={index} />
 )

 return (
  &lt;>
   &lt;Ueberschrift content="Pluto" />
   {createContents()}
  &lt;/>
 )
}

const Ueberschrift = props => &lt;h2>{props.content}&lt;/h2>

const Absatz = props => {
 return (
  &lt;div className="Absatz">
   &lt;p>{props.content}&lt;/p>
   &lt;p className="charCount">{String(props.content).length} Chars&lt;/p>
  &lt;/div>
 )
}

export default App;
