interface User {
 name: string;
 age: number;
}

type PartialUser = Partial&lt;User>;

const myUser: PartialUser = {
 name: "Max",
}

console.log(myUser)

interface User {
 name: string;
 age?: number;
}

type FullUser = Required&lt;User>;

const myUser: FullUser = {
 name: "Max",
 age: 25
}

console.log(myUser)

interface User {
 name: string;
 age: number;
}

type ImmutableUser = Readonly&lt;User>;

const myUser: ImmutableUser = {
 name: "Max",
 age: 25
}

myUser.name = "Maxi";   // -> Fehlermeldung
console.log(myUser)

interface User {
 name: string;
 age: number;
 address: string;
 email: string;
}

type PartialUser = Pick&lt;User, 'name' | 'age'>;

const myUser: PartialUser = {
 name: "Max",
 age: 25
}

console.log(myUser)

interface User {
 name: string;
 age: number;
 address: string;
 email: string;
}

type PartialUser = Omit<User, 'address' | 'email'>;

const myUser: PartialUser = {
 name: "Max",
 age: 25
}

console.log(myUser)
