ctx.beginPath();

ctx.moveTo(50, 20);
ctx.lineTo(150, 20);
ctx.lineTo(250, 80);
ctx.lineTo(250, 130);

ctx.moveTo(50, 50);
ctx.lineTo(50, 80);
ctx.lineTo(120, 130);
ctx.lineTo(220, 130);

ctx.stroke();

const c = document.querySelector('#myCanvas');
const ctx = c.getContext('2d');
const amp = 60;
const streckung = 1.5;

ctx.beginPath();
ctx.moveTo(0, c.height / 2);
for (let i = 0; i < c.width; i++) {
 let temp = i * streckung;
 temp = Math.sin(temp / 180 * Math.PI);
 temp *= amp;
 temp += c.height / 2;
 ctx.lineTo(
  i,
  temp
 )
}
ctx.stroke();

const c = document.querySelector('#myCanvas');
const ctx = c.getContext('2d');

const path = [];

const render = () => {
 ctx.clearRect(0, 0, c.width, c.height);
 ctx.beginPath();
 ctx.moveTo(path[0].x, path[0].y);
 for (let point of path) {
  ctx.lineTo(point.x, point.y)
 }
 ctx.stroke();
}

const addPoint = evt => {
 path.push({
  x: evt.layerX,
  y: evt.layerY,
 })
 render();
}

c.addEventListener('mousemove', addPoint);
