'use strict';

import express from 'express';
import {createServer as createViteServer} from 'vite';
import {promises as fs} from 'fs';

const server = express();
let viteServer;

const addCatchAll = () => server.use((req, res) => {
 let template
 fs.readFile('index.html', 'utf-8').then(
  payload => viteServer.transformIndexHtml(req.originalUrl, payload) 
 ).then(
  (result) => template = result
 ).then(
  () => viteServer.ssrLoadModule('/src/entry-server.jsx')
 ).then(
  ({render}) => render(req.originalURL)
 ).then(
  ({html: appHtml}) => {
   const html = template.replace('&lt;!--ssr-outlet-->', appHtml);
   res.status(200).set({'Content-Type': 'text/html'}).end(html);
  }
 ).catch(
  console.warn
 )
})

const init = () => {
 createViteServer({
  server: {middlewareMode: true},
  appType: 'custom',
 }).then(
  result => {
   viteServer = result;
   server.use(viteServer.middlewares)
  }
 ).then(
  addCatchAll
 ).then(
  () => server.listen(8001, err => {
   if (err) console.log(err);
   else console.log('Server läuft');
  })
 ).catch(
  console.warn
 )
}

init();

import React, { StrictMode } from 'react';
import { renderToString } from 'react-dom/server';
import App from './App.jsx';

export function render(url) {
 const html = renderToString(
  &lt;StrictMode>
   &lt;App />
  &lt;/StrictMode>
 );
 return { html };
}
