const main = document.querySelector('main');

const elContent = (content, parent) => {
 const container = document.createElement('div');
 container.className = 'container';
 parent.append(container);

 container.setContent = newContent => {
  content = newContent;
  render();
 }

 const render = () => {
  container.innerHTML = '';
  const absatz = document.createElement('p');
  absatz.innerHTML = content;
  container.append(absatz);
 }

 render();

 return container;
}

let myParagraph = elContent('Hallo Welt', main);
let yourParagraph = elContent('Javascript rockt', main);

myParagraph.setContent('Dies ist neuer Inhalt.')
