const handleSubmit = evt => {
 evt.preventDefault();
 let data = new FormData(myForm);
 console.log(data);  // -> FormData { myText → "Hallo", city → "B" }
}

const myForm = document.querySelector('form');
myForm.addEventListener('submit', handleSubmit);

const handler = evt => {
 let data = new FormData();
 data.set('Hallo', 'Welt');
 data.set('rnd', ~~(Math.random()*100));

 console.log(data.get('rnd'));  // -> 71
 console.log(data.has('rnd'));  // -> true
 console.log(data); // -> FormData { Hallo → "Welt", rnd → "71" }

 for(let el of data ) {
  console.log(el); // -> ["Hallo","Welt"]
                   // -> [ "rnd", "71" ]
 }
}
const myBtn = document.querySelector('button');
myBtn.addEventListener('click', handler);
