const meinAuto = {
    marke: 'Hyundai',
    modell:'i20',
    leistungKW: 62,
    farbe: 'braun',
    leasing: false
}
meinAuto.leistungKW = 100;

// Objekt kann ab hier nicht mehr verändert werden.
Object.freeze(meinAuto);
meinAuto.leistungKW = 123;
console.log(meinAuto.leistungKW);  // -> 100

const meinAuto = {
    marke: 'Hyundai',
    modell: 'i20',
    motor: {
        leistungKW: 62
    }
}

Object.freeze(meinAuto);
meinAuto.modell = 'Kona';
meinAuto.motor.leistungKW = 123;

console.log(meinAuto.modell);            // -> i20 (unverändert)
console.log(meinAuto.motor.leistungKW);  // -> 123 (verändert)

const meinAuto = {
 marke: 'Hyundai',
 modell: 'i20',
 leistungKW: 62
}

console.log(Object.isFrozen(meinAuto));  // -> false

Object.freeze(meinAuto);
console.log(Object.isFrozen(meinAuto));  // -> true

const meinAuto = {
 marke: 'Hyundai',
 modell: 'i20',
 leistungKW: 62
}

Object.seal(meinAuto);
meinAuto.leistungKW = 123;
meinAuto.farbe = 'weiß';

console.log(meinAuto.leistungKW);
console.log(meinAuto.farbe);
