const elements = document.querySelectorAll('.container');
const heightAll = 500;

elements.forEach(el => {
 el.style.backgroundColor = `hsl(${~~(Math.random() * 40)}, 100%, 50%)`;
 el.style.height = `${heightAll / elements.length}px`;
 el.style.fontSize = `${heightAll / elements.length / 4}px`;
})
