// Component "Abschnitt.js"

const Abschnitt = (
 contents = [],
 header = '',
 parent = false
) => {
 const container = document.createElement('div');
 parent.append(container);

 const elHeader = document.createElement('h2');
 elHeader.innerHTML = header;
 container.append(elHeader);

 contents.forEach(content => {
  const elAbsatz = document.createElement('p');
  elAbsatz.innerHTML = content;
  container.append(elAbsatz);
 })

 return container;
}

export default Abschnitt;

// Verwendung der Komponente

import Absatz from './components/Abschnitt.js';

const contents = [
 "Componenten sind wiederverwendbare Bausteine.",
 "Komplexe Anwendungen in überschaubare Teile zerlegen.",
 "Wiederverwendbarkeit von Code, spart Entwicklungszeit.",
 "Klare Struktur in den Code bringen, Lesbarkeit verbessern."
];

const main = document.querySelector('main');
const myAbsatz = Absatz(contents, 'Components', main);
