'use strict';

const template = document.createElement('div');

class Random extends HTMLElement {
 constructor() {
  super();
  this.root = this.attachShadow({
   mode: 'closed'
  })
  this.root.append(template.cloneNode(true));
 }

 // Eventlistener für Mounting
 connectedCallback() {
  const elContent = document.createElement('p');
  elContent.innerHTML = Math.floor(Math.random() * 1000);
  this.root.append(elContent);
 }
}

customElements.define('rnd-number', Random);
