const myPromise = new Promise((resolve, reject) => {
 let z = Math.floor(Math.random() * 100);
 if (z < 50) resolve(z);
 else reject(z)
});

myPromise.then(
 result => {
  console.log('resolve', result);
  return result *2;
 }
