const c = document.querySelector('#myCanvas');
const ctx = c.getContext('2d');

ctx.fillStyle = '#000';
ctx.fillRect(0, 0, c.width, c.height);

ctx.fillStyle = '#ff0';
ctx.font = '15px arial';
ctx.fillText('Beschreibung 1:', 30, 20);

ctx.save();
ctx.fillStyle = '#f00';
ctx.font = '10px times';
ctx.fillText('Irgendein Text', 150, 20);

ctx.restore();
ctx.fillText('Beschreibung 2:', 30, 50);

ctx.save();
ctx.fillStyle = '#f00';
ctx.font = '10px times';
ctx.fillText('mehr Text', 150, 50);

ctx.restore();
ctx.fillText('Beschreibung 3:', 30, 80);

ctx.save();
ctx.fillStyle = '#f00';
ctx.font = '10px times';
ctx.fillText('letzter Text', 150, 80);

const c = document.querySelector('#myCanvas');
const ctx = c.getContext('2d');

let num = 10;

ctx.translate(c.width / 2, c.height / 2);
for (let i = 0; i < num; i++) {
 ctx.rotate((2 * Math.PI) / num);
 ctx.save()
  for (let i = 0; i < 10; i++) {
   ctx.fillRect(0, 0, 70, 5);
   ctx.translate(20, 0);
   ctx.rotate(-.2);
   ctx.scale(.8, .8)
  }
 ctx.restore()
}

const c = document.querySelector('#myCanvas');
const ctx = c.getContext('2d');
ctx.font = '15px arial';

ctx.fillStyle = '#000';
ctx.fillRect(0, 0, c.width, c.height);

ctx.fillStyle = '#ff0';
ctx.save();            
ctx.fillStyle = '#f00';
ctx.save();
ctx.fillStyle = '#0f0';
ctx.save();
ctx.fillStyle = '#88f';
ctx.save();
ctx.fillStyle = '#809';
ctx.save();
ctx.fillStyle = '#809';

ctx.fillText('Text 1:', 30, 20);
ctx.restore();
ctx.fillText('Text 2', 30, 50);            
ctx.restore();
ctx.fillText('Text 3', 30, 80);            
ctx.restore();
ctx.fillText('Text 4', 30, 110);            
ctx.restore();
ctx.fillText('Text 5', 30, 140);            
ctx.restore();
