const c = document.querySelector('#myCanvas');
const ctx = c.getContext('2d');

ctx.shadowColor = 'rgba(0,0,0,.5)'
ctx.shadowBlur = 10;
ctx.shadowOffsetX = -5;
ctx.shadowOffsetY = 5;

ctx.beginPath();
ctx.fillStyle = '#800'
ctx.rect(30, 30, 90, 90);
ctx.fill();

ctx.shadowOffsetX = 5;

ctx.beginPath();
ctx.fillStyle = '#080'
ctx.arc(225, 75, 50, 0, 2 * Math.PI);
ctx.fill();
