let myNum: number =  1234;
let myString = 'Hello World';

console.log(myNum, myString)

let myValue: string | number = 23;
console.log(myValue);

myValue = "Hallo";
console.log(myValue);

let myArr: Array&lt;number> = [12, 45, 78];
let myArr2: Array&lt;string> = ['Merkur', 'Venus', 'Erde', 'Mars'];
let myArr3: Array&lt;any> = ['Merkur', 132, true];

console.log(myArr);
console.log(myArr2);
console.log(myArr3);

let myObj: { vName: string, nName: string, yearOfBirth: number } = {
 vName: 'Max',
 nName: 'Mustermann',
 yearOfBirth: 1990,
 place: 'Musterhausen'  // -> Fehlermeldung
}

console.log(myObj);

class Person {
 prename: string;
 surname: string;

 constructor(prename: string, surname: string) {
  this.prename = prename;
  this.surname = surname;
 }
}

let myPerson = new Person("Max", "Mustermann");
console.log(myPerson);
