let num = 200;
const elSvg = document.querySelector('svg');
const createNumber = (min, max) => ~~(Math.random() * (max - min + 1) + min);

for (let i = 0; i < num; i++) {
 let size = createNumber(10, 30);
 let elRect = document.createElementNS("http://www.w3.org/2000/svg", "rect");

 elRect.setAttribute('x', createNumber(0, 800));
 elRect.setAttribute('y', createNumber(0, 600));
 elRect.setAttribute('width', size);
 elRect.setAttribute('height', size);
 elRect.setAttribute('rx', size / 5);
 elRect.setAttribute('ry', size / 5);
 elRect.setAttribute('fill', `hsl(${createNumber(0, 360)},50%,70%)`);
 elSvg.append(elRect);
}
