const loadURL = url => {
 return fetch(url).then(
  res => res.json()
 )
}

Promise.race([
 loadURL('https://dummyjson.com/products/1'),
 loadURL('https://dummyjson.com/products/2'),
 loadURL('https://dummyjson.com/products/3'),
 loadURL('https://dummyjson.com/products/4'),
 loadURL('https://dummyjson.com/products/5'),
]).then(
 res => console.log(res)
).catch(
 err => console.warn(err)
)

const loadURL = url => {
