let dbUsers = db.connection.use('plixl_users'); 

dbUsers.insert({
 "_id": "_design/index",
 "views": {
  "by_crdate": {
   "map": "function (doc) {\n  emit(doc.createdAt, doc);\n}"
  },
  "by_firstname": {
   "map": "function (doc) {\n  emit(doc.firstName, doc);\n}"
  },
  "by_lastname": {
   "map": "function (doc) {\n  emit(doc.lastName, doc);\n}"
  },
  "by_fullname": {
   "map": "function (doc) {\n  emit([doc.lastName, doc.firstName], doc);\n}"
  }
 },
 "language": "javascript"
})
