const removeDocument = (db, id) => {
 let myTransaction = db.transaction(objStoreName, 'readwrite');
 let myObjectStore = myTransaction.objectStore(objStoreName);
 let myRequest = myObjectStore.delete(id);

 myRequest.addEventListener('success', evt => {
  console.log('success', evt.target);
 })

 myRequest.addEventListener('error', evt => {
  console.log('error', evt);
 })
}

const handleSuccess = evt => {
 removeDocument(evt.target.result, 10);
}

const init = () => {
 requestDB = window.indexedDB.open(dbName);
 requestDB.addEventListener('success', handleSuccess);
}

init();

const removeDocument = (db, id) => {
 let myRequest = db.transaction(objStoreName, 'readwrite')
  .objectStore(objStoreName)
  .delete(id);
}
