import { useState } from 'react';

const App = () => {
 let [data, setData] = useState(100);

 const changeData = () => {
  let num = ~~(Math.random() * 100);
  setData(num); 
 }

 return (
  &lt;>
   &lt;p> {data} &lt;/p>
   &lt;p> &lt;button onClick={changeData}>Change&lt;/button> &lt;/p>
  &lt;/>
 }
}
