// Javascript
const el = document.querySelector('svg rect');
let counter = 0;
let speed = 3;

const doStuff = () => {
 counter = (counter + speed) % 360;
 el.setAttribute('fill', `hsla(${counter},100%,50%)`)

 requestAnimationFrame(doStuff)
}

requestAnimationFrame(doStuff)

// Javascript
const el = document.querySelector('svg .animateMe');
let counter = 0;
let speed = 1;

const doStuff = () => {
 let posY = Math.abs(Math.sin(counter / 10) * 50)
 el.setAttribute('x', counter - 40)
 el.setAttribute('y', 160 - posY)

 counter = ((counter + speed) % 240);
 requestAnimationFrame(doStuff)
}

requestAnimationFrame(doStuff)
