let myDate = new Date();
console.log(myDate);  // -> Aktuelles Datum

let yourDate = new Date(2023, 3, 10, 15);
console.log(yourDate);  // -> 10. April 2023, 15Uhr

let thirdDate = new Date('April 20, 2023 15:00:00');
console.log(thirdDate);  // -> 20. April 2023 15Uhr

let otherDate = new Date(0);
console.log(otherDate);  // -> 1.1.1970, 0Uhr

let myDate = new Date(2023, 3, 10, 15);
console.log(myDate.getMonth()); // -> 3
myDate.setDate(40);        
console.log(myDate.getMonth()); // -> 4
