const OrcProto = {
 health: 100,
 strength: 140,
 speed: 60
}

const createOrc = name => {
 const orc = Object.create(OrcProto);
 orc.name = name;
 return orc;
}

let orc1 = createOrc('Hark');
let orc2 = createOrc('Gnoff');

orc1.health -= 10;

console.log(orc1);
console.log(orc1.health);
console.log(orc2);
console.log(orc2.health);
