ctx.beginPath();

let myGradient = ctx.createLinearGradient(100, 0, 200, 0);
myGradient.addColorStop(0, '#f00');
myGradient.addColorStop(1, '#00f');
ctx.fillStyle = myGradient;

ctx.rect(20, 20, 130, 30);
ctx.rect(160, 20, 120, 30);
ctx.rect(40, 60, 220, 70);

ctx.fill();

ctx.beginPath();

let myGradient = ctx.createLinearGradient(0, 20, 0, c.height);
myGradient.addColorStop(0, '#7f81cf');
myGradient.addColorStop(.5, '#313277');
myGradient.addColorStop(.5, '#ccc');
myGradient.addColorStop(1, '#888');
ctx.fillStyle = myGradient;

ctx.rect(20, 20, c.width - 40, c.height - 40);

ctx.fill();

ctx.beginPath();

let myGradient = ctx.createRadialGradient(
 c.width / 2, c.height / 2, 50,
 c.width / 2, c.height / 2, 150
);
myGradient.addColorStop(0, '#f00');
myGradient.addColorStop(1, '#00f');

for (let i = .1; i < .9; i += .1) {
 myGradient.addColorStop(i, '#00f');
 myGradient.addColorStop(i, '#000');
 myGradient.addColorStop(i + .02, '#000');
 myGradient.addColorStop(i + .02, '#00f');
}
ctx.fillStyle = myGradient;

ctx.rect(20, 10, c.width - 40, 40);
ctx.rect(20, 55, c.width - 40, 40);
ctx.rect(20, 100, c.width - 40, 40);

ctx.fill();

ctx.beginPath();

let myGradient = ctx.createConicGradient(
 0,
 c.width / 2, c.height / 2
);

for (let i = 0; i < .9; i += .1) {
 myGradient.addColorStop(i, '#000');
 myGradient.addColorStop(i + .05, '#00f');
 myGradient.addColorStop(i + .05, '#000');
 myGradient.addColorStop(i + .06, '#000');
}

ctx.fillStyle = myGradient;

ctx.rect(20, 10, c.width - 40, 40);
ctx.rect(20, 55, c.width - 40, 40);
ctx.rect(20, 100, c.width - 40, 40);

ctx.fill();
