const meinAuto = {
    marke: 'Hyundai',
    modell: 'i20',
    leistungKW:62,
    ganzerName() {
        return `${this.marke} ${this.modell}`
    },
    leistungPS(decimals){
        return (this.leistungKW / .73).toFixed(decimals);
    }
}

console.log(meinAuto.ganzerName());  // -> Hyundai i20
console.log(meinAuto.leistungPS(2));  // -> 84.93
