// Server
const people = [
 { id: 1, name: "John Doe", age: 30, weight: 80 },
 { id: 2, name: "Jane Doe", age: 25, weight: 60 }
];

server.get('/user/:id', (request, response) => {
 let person = people.find(item => item.id == request.params.id);
 response.json({
  status: 'ok',
  data: person
 })
})

server.listen(8000, err => console.log(err || 'Server läuft'));

// Client
fetch('/user/1').then(
 res => res.text()  
).then(
 console.log  
 // -> {"status":"ok","data":
 // ->  {"id":1,"name":"John Doe","age":30,"weight":80}
 // -> } 
).catch(
 console.warn
)

// Server
const people = [
 { id: 1, name: "John Doe", age: 30, weight: 80 },
 { id: 2, name: "Jane Doe", age: 25, weight: 60 }
];

server.get('/user/:id/profile/:attr', (request, response) => {
 let person = people.find(item => item.id == request.params.id);

 response.json({
  status: 'ok',
  data: person[request.params.attr]
 })
})

server.listen(80, err => console.log(err || 'Server läuft'));

