const c = document.querySelector('#myCanvas');
const ctx = c.getContext('2d');

const cSource = document.createElement('canvas');
const ctxSource = cSource.getContext('2d')
cSource.width = 100;
cSource.height = 100;
document.body.append(cSource);

// Quelle zeichnen
ctxSource.lineWidth = 3;
ctxSource.beginPath();
for (let i = 10; i < cSource.width / 2; i += 6) {
 ctxSource.moveTo(cSource.width / 2 + i, cSource.height / 2);
 ctxSource.arc(cSource.width / 2, cSource.height / 2, i, 0, 2 * Math.PI);
}
ctxSource.stroke();

// Bilder zeichnen
ctx.drawImage(cSource, 20, 20);
ctx.drawImage(cSource, 130, 20, 50, 50);
ctx.drawImage(cSource,
 0, 0, 50, 50,
 180, 20, 100, 100
);
