class Person {
 constructor(name, geb) {
  this.name = name;
  this.geb = geb;
 }
 sayHello() {
  console.log(`Hello, my name is ${this.name} and I am ${this.age()} years old.`);
 }
 age(){
  return (new Date().getFullYear() - this.geb)
 }
}

