let myArray = [];
console.log(myArray);  // -> []

let yourArray = [21, 54, 87, 32, 65];
console.log(yourArray);  // -> [21, 54, 87, 32, 65]

let myArray = [21, 54, 87, 32, 65];
console.log(myArray[2]);  // -> 87

myArray[2] = 100;
console.log(myArray);  // -> [ 21, 54, 100, 32, 65 ]

myArray[8] = 100;
console.log(myArray);  // -> [ 21, 54, 100, 32, 65, <3 empty items>, 100 ]
