// Eltern-Element auswählen
const parent = document.createElement('ul');
document.querySelector('#first').append(parent);

const moveThis = document.querySelector('#third ul > li:nth-of-type(1)');
const moveThat = document.querySelector('#third ul > li:nth-of-type(2)');
const moveMore = document.querySelector('#third ul > li:nth-of-type(3)');

parent.append(moveThis, moveThat, moveMore);

// Eltern-Element auswählen
const parent = document.createElement('ul');
document.querySelector('#first').append(parent);

const moveThis = [...document.querySelectorAll('#third ul > li')];

parent.append(...moveThis);

const parent = document.createElement('ul');
document.querySelector('#first').prepend(parent);

const moveThis = [...document.querySelectorAll('#third ul > li')];

parent.prepend(...moveThis);

const moveToHere = document.querySelector('#second');
const moveMe = document.querySelector('#third');

moveToHere.parentNode.insertBefore(moveMe, moveToHere);
