const c = document.querySelector('#myCanvas');
const ctx = c.getContext('2d');

ctx.lineWidth = 20;

ctx.beginPath();

ctx.moveTo(80,10);
ctx.lineTo(80,40);

ctx.moveTo(220,10);
ctx.lineTo(220,40);

ctx.moveTo(40,20);
ctx.lineTo(40,60);
ctx.bezierCurveTo(40,150,260,150,260,60);
ctx.lineTo(260,20);

ctx.stroke();

const c = document.querySelector('#myCanvas');
const ctx = c.getContext('2d');

ctx.lineWidth = 20;
ctx.lineCap = 'round'

ctx.beginPath();
ctx.moveTo(80,20);
ctx.lineTo(80,40);

ctx.moveTo(220,20);
ctx.lineTo(220,40);

ctx.moveTo(40,30);
ctx.lineTo(40,60);
ctx.bezierCurveTo(40,150,260,150,260,60);
ctx.lineTo(260,30);
ctx.stroke();

const c = document.querySelector('#myCanvas');
const ctx = c.getContext('2d');

ctx.lineWidth = 20;
ctx.lineJoin = 'round'

ctx.beginPath();
ctx.moveTo(150,20);
ctx.lineTo(245,75);
ctx.lineTo(150,130);
ctx.lineTo(55,75);
ctx.closePath();

ctx.stroke();
