const c = document.querySelector('canvas');
let ctx = c.getContext('2d');

ctx.globalCompositeOperation = 'source-over';

ctx.fillStyle = '#f00';
ctx.fillRect(100, 20, 200, 100);
ctx.fillStyle = '#0f0';
ctx.fillRect(250, 60, 200, 100);
ctx.fillStyle = '#00f';
ctx.fillRect(150, 80, 200, 100);
