const createNumber = (min, max) => 
 Math.floor(Math.random() * (max - min + 1) + min);

const num = createNumber(10, 20);

console.log(num);

const createNumber = (min = 0, max = 100, dec = 1) => {
 let multiplyer = 10 ** dec;
 let rnd = Math.random();
 rnd = rnd * (max - min + (1 / multiplyer)) + min;
 rnd = Math.floor(rnd * multiplyer) / multiplyer;
 return rnd
};

const num = createNumber(10, 20, 2);

console.log(num);

const createNumber = (min, max) => ~~(Math.random() * (max - min + 1) + min);

const createColor = ({
 minHue = 0, maxHue = 360,
 minSat = 0, maxSat = 100,
 minLight = 50, maxLight = 50,
 minAlpha = 1, maxAlpha = 1
} = {}) => {
 let color = 'hsl(';
 color += `${createNumber(minHue, maxHue)},`;
 color += `${createNumber(minSat, maxSat)}%,`
 color += `${createNumber(minLight, maxLight)}%,`
 color += `${Math.random() * (maxAlpha - minAlpha) + minAlpha})`
 return color;
}

let myColor = createColor();
console.log(myColor);

// Grüntöne mit wenig Sättigung
myColor = createColor({minHue: 100, maxHue:140, minSat: 20, maxSat: 40});
console.log(myColor);

// Wechselnde Farben mit 20% Deckung
myColor = createColor({minAlpha: .2, maxAlpha: .2});
console.log(myColor);
