import express from 'express';
const server = express();
server.use(express.json())

server.use('/', (req, res) => {
 console.log(req.body);
 res.send('Hallo Welt')
});

server.listen(8000, err => console.log(err || 'Server läuft'));

import express from 'express';
const server = express();
server.use(express.urlencoded({ extended: true }))

server.use('/', (req, res) => {
 console.log(req.body);
 res.send('Hallo Welt')
});

server.listen(8000, err => console.log(err || 'Server läuft'));
