import express from 'express';
import { WebSocketServer } from 'ws';

const server = express();
const wss = new WebSocketServer({ port: 8000 });

const handleMsg = () => console.log('Nachricht empfangen');

wss.on('listening', () => {
 console.log('SocketServer ist bereit und wartet auf Clients')
});

wss.on('connection', socket => {
 socket.on('error', console.error);
 socket.on('message', handleMsg);
 socket.send('message');

 socket.on('close', socket => {
  console.log('Verbindung beendet');
 });
});

server.use(express.static('public', {
 extensions:['html']
}));

const init = () => {
 server.listen(80, err => console.log(err || 'Server läuft'));
}

init();

const socket = new WebSocket("ws://localhost:8000");

// Connection opened
socket.addEventListener("open", evt => {
 console.log('Verbindung wurde hergestellt');
});

socket.addEventListener("message", evt => {
 console.log('Nachricht empfangen');
});

// Nachricht zur Gegenseite senden
socket.send(JSON.stringify({
 type: 'myMsg',
 payload: {
  marke: 'Hyundai',
  modell: 'i20',
  leistungKW: 62,
 }
}))

// Auf Nachricht reagieren
const handleMyMsg = data => {
 console.log('Auto', data);
}

const handleOtherMsg = data => { }

socket.on('message', data => {
 data = JSON.parse(data.toString());

 switch (data.type) {

  case 'myMsg':
   handleMyMsg(data.payload)
  break;

  case 'otherMsg':
   handleOtherMsg(data.payload)
  break;

  default:
  break;

 }

});

const wss = new WebSocketServer({ port: 8000 });

wss.clients.forEach(socket => {
 socket.send(JSON.stringify({
  type: 'broadcast',
  payload: data
 }));
});
